Select Id, Phone, Email, UserId, HelpId, Message, Address, Location, Createddate, Requesteddate, Latitude, Longitude, Duration, Status, VolunteerId, Language, Meetingtype from blinx.t_help_request
USE blinx;

CREATE TABLE `t_help_request` (
  `Id` int(11) NOT NULL DEFAULT '0',
  `Phone` varchar(15) DEFAULT NULL,
  `Email` varchar(100) DEFAULT NULL,
  `UserId` int(11) DEFAULT NULL,
  `HelpId` int(3) DEFAULT NULL,
  `Message` varchar(200) DEFAULT NULL,
  `Address` varchar(200) DEFAULT NULL,
  `Location` varchar(200) DEFAULT NULL,
  `Createddate` datetime DEFAULT NULL,
  `Requesteddate` datetime DEFAULT NULL,
  `Latitude` varchar(200) DEFAULT NULL,
  `Longitude` varchar(200) DEFAULT NULL,
  `Duration` int(1) DEFAULT NULL,
  `Status` varchar(3) DEFAULT NULL,
  `VolunteerId` int(11) DEFAULT NULL,
  `Language` int(3) DEFAULT NULL,
  `Meetingtype` int(3) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


